pair A, B, C, D, E, F, O;
real s = 2/sqrt(3) - 1, r = (9 - 4*sqrt(3))/33;

A = 2/sqrt(3)*dir(150);
B = 2/sqrt(3)*dir(30);
C = 2/sqrt(3)*dir(270);
